import React, { useState, useEffect } from 'react';
import { useParams, useNavigate } from 'react-router-dom';
import API from '../../api';
import { useAuth } from '../../context/AuthContext';
import { ArrowLeft, CheckCircle2, Circle, Play, FileText, ChevronDown, ChevronRight, Clock, Lock } from 'lucide-react';
import toast from 'react-hot-toast';

export default function CourseView() {
  const { courseId } = useParams();
  const { user } = useAuth();
  const navigate = useNavigate();
  const [course, setCourse] = useState(null);
  const [enrollment, setEnrollment] = useState(null);
  const [activeLesson, setActiveLesson] = useState(null);
  const [expandedModules, setExpandedModules] = useState({});
  const [loading, setLoading] = useState(true);

  useEffect(() => {
    Promise.all([
      API.get(`/api/courses/${courseId}`),
      API.get('/api/enrollments', { params: { course_id: courseId } }),
    ]).then(([c, e]) => {
      setCourse(c.data);
      const myEnrollment = e.data.find(en => en.student_id === user?.user_id);
      setEnrollment(myEnrollment);
      const expanded = {};
      c.data.modules?.forEach(m => { expanded[m.module_id] = true; });
      setExpandedModules(expanded);
      const firstUncompletedLesson = c.data.modules?.flatMap(m => m.lessons)?.find(l => !myEnrollment?.completed_lessons?.includes(l.lesson_id));
      if (firstUncompletedLesson) setActiveLesson(firstUncompletedLesson);
      else if (c.data.modules?.[0]?.lessons?.[0]) setActiveLesson(c.data.modules[0].lessons[0]);
    }).catch(() => toast.error('Error loading course')).finally(() => setLoading(false));
  }, [courseId]);

  const completeLesson = async (lessonId) => {
    try {
      const res = await API.post(`/api/lessons/${lessonId}/complete`);
      setEnrollment(prev => ({
        ...prev,
        completed_lessons: [...(prev?.completed_lessons || []), lessonId],
        progress: res.data.progress
      }));
      toast.success('Lesson completed!');
    } catch (e) { toast.error(e.response?.data?.detail || 'Error'); }
  };

  const isCompleted = (lessonId) => enrollment?.completed_lessons?.includes(lessonId);

  if (loading) return <div className="flex items-center justify-center h-64"><div className="animate-spin w-8 h-8 border-4 border-indigo-500 border-t-transparent rounded-full" /></div>;
  if (!course) return <p>Course not found</p>;

  return (
    <div className="flex flex-col md:flex-row h-[calc(100vh-7rem)] -m-6 md:-m-8" data-testid="course-view">
      {/* Sidebar - Module List */}
      <div className="w-full md:w-80 border-r border-[#E7E5E4] bg-white overflow-y-auto shrink-0">
        <div className="p-4 border-b border-[#E7E5E4]">
          <button onClick={() => navigate('/student')} className="flex items-center gap-1 text-sm text-[#6366F1] hover:underline mb-3" data-testid="back-to-dashboard"><ArrowLeft size={14} />Back</button>
          <h2 className="font-bold text-[#1C1917]" style={{ fontFamily: 'Manrope' }}>{course.title}</h2>
          <div className="flex items-center gap-2 mt-2">
            <div className="flex-1 bg-[#F5F5F4] rounded-full h-1.5 overflow-hidden">
              <div className="h-full bg-[#6366F1] rounded-full" style={{ width: `${enrollment?.progress || 0}%` }} />
            </div>
            <span className="text-xs font-semibold text-[#6366F1]">{enrollment?.progress || 0}%</span>
          </div>
        </div>

        <div className="py-2">
          {course.modules?.map((m, mi) => (
            <div key={m.module_id}>
              <button onClick={() => setExpandedModules(prev => ({ ...prev, [m.module_id]: !prev[m.module_id] }))} className="w-full flex items-center justify-between px-4 py-3 hover:bg-[#F5F5F4] transition-colors">
                <span className="text-sm font-semibold text-[#1C1917] text-left">{mi + 1}. {m.title}</span>
                {expandedModules[m.module_id] ? <ChevronDown size={16} className="text-[#A8A29E]" /> : <ChevronRight size={16} className="text-[#A8A29E]" />}
              </button>
              {expandedModules[m.module_id] && (
                <div className="pb-1">
                  {m.lessons?.map((l) => (
                    <button key={l.lesson_id} onClick={() => setActiveLesson(l)} className={`w-full flex items-center gap-3 px-6 py-2.5 text-left transition-colors ${activeLesson?.lesson_id === l.lesson_id ? 'bg-[#EEF2FF] border-r-2 border-[#6366F1]' : 'hover:bg-[#F5F5F4]'}`} data-testid={`lesson-nav-${l.lesson_id}`}>
                      {isCompleted(l.lesson_id) ? <CheckCircle2 size={16} className="text-[#10B981] shrink-0" /> : <Circle size={16} className="text-[#D6D3D1] shrink-0" />}
                      <div className="flex-1 min-w-0">
                        <p className={`text-sm truncate ${activeLesson?.lesson_id === l.lesson_id ? 'font-medium text-[#312E81]' : 'text-[#57534E]'}`}>{l.title}</p>
                        <div className="flex items-center gap-2 text-xs text-[#A8A29E]">
                          {l.type === 'video' ? <Play size={10} /> : <FileText size={10} />}
                          <span>{l.duration}</span>
                        </div>
                      </div>
                    </button>
                  ))}
                </div>
              )}
            </div>
          ))}
        </div>
      </div>

      {/* Main Content - Lesson Player */}
      <div className="flex-1 overflow-y-auto bg-[#FAFAF9] p-6 md:p-8">
        {activeLesson ? (
          <div className="max-w-3xl mx-auto animate-fade-in" data-testid="lesson-content">
            <div className="flex items-center gap-2 mb-1 text-xs text-[#A8A29E]">
              <span className={`px-2 py-0.5 rounded ${activeLesson.type === 'video' ? 'bg-blue-50 text-blue-600' : 'bg-green-50 text-green-600'}`}>{activeLesson.type}</span>
              <span className="flex items-center gap-1"><Clock size={10} />{activeLesson.duration}</span>
            </div>
            <h1 className="text-2xl font-bold text-[#1C1917] mb-6" style={{ fontFamily: 'Manrope' }}>{activeLesson.title}</h1>

            {activeLesson.youtube_url && (
              <div className="aspect-video rounded-xl overflow-hidden mb-6 bg-black">
                <iframe src={activeLesson.youtube_url} className="w-full h-full" allow="accelerometer; autoplay; clipboard-write; encrypted-media; gyroscope; picture-in-picture" allowFullScreen title={activeLesson.title} />
              </div>
            )}

            <div className="bg-white rounded-xl border border-[#E7E5E4] p-6 md:p-8 prose prose-sm max-w-none" data-testid="lesson-text-content">
              <div dangerouslySetInnerHTML={{ __html: activeLesson.content }} />
            </div>

            <div className="mt-6 flex items-center justify-between">
              {isCompleted(activeLesson.lesson_id) ? (
                <div className="flex items-center gap-2 text-[#10B981]">
                  <CheckCircle2 size={20} />
                  <span className="text-sm font-medium">Completed</span>
                </div>
              ) : (
                <button onClick={() => completeLesson(activeLesson.lesson_id)} className="flex items-center gap-2 px-6 py-2.5 bg-[#312E81] text-white rounded-lg font-medium text-sm hover:bg-[#312E81]/90 active:scale-[0.98] transition-all" data-testid="mark-complete-btn">
                  <CheckCircle2 size={16} />Mark as Complete
                </button>
              )}

              <button onClick={() => {
                const allLessons = course.modules?.flatMap(m => m.lessons) || [];
                const idx = allLessons.findIndex(l => l.lesson_id === activeLesson.lesson_id);
                if (idx < allLessons.length - 1) setActiveLesson(allLessons[idx + 1]);
              }} className="flex items-center gap-1 text-sm text-[#6366F1] font-medium hover:underline" data-testid="next-lesson-btn">
                Next Lesson <ChevronRight size={16} />
              </button>
            </div>
          </div>
        ) : (
          <div className="text-center py-12">
            <p className="text-[#A8A29E]">Select a lesson to start learning</p>
          </div>
        )}
      </div>
    </div>
  );
}
