import React, { useState } from 'react';
import { NavLink, useLocation } from 'react-router-dom';
import { useAuth } from '../context/AuthContext';
import { LayoutDashboard, BookOpen, Users, GraduationCap, FileQuestion, ClipboardList, BarChart3, Award, Bell, Settings, ChevronLeft, ChevronRight, LogOut, Menu } from 'lucide-react';

const adminLinks = [
  { to: '/admin', icon: LayoutDashboard, label: 'Dashboard', end: true },
  { to: '/admin/courses', icon: BookOpen, label: 'Courses' },
  { to: '/admin/students', icon: Users, label: 'Students' },
  { to: '/admin/instructors', icon: GraduationCap, label: 'Instructors' },
  { to: '/admin/quizzes', icon: FileQuestion, label: 'Quizzes' },
  { to: '/admin/assignments', icon: ClipboardList, label: 'Assignments' },
  { to: '/admin/analytics', icon: BarChart3, label: 'Analytics' },
  { to: '/admin/certificates', icon: Award, label: 'Certificates' },
  { to: '/admin/notifications', icon: Bell, label: 'Notifications' },
  { to: '/admin/settings', icon: Settings, label: 'Settings' },
];

const instructorLinks = [
  { to: '/instructor', icon: LayoutDashboard, label: 'Dashboard', end: true },
  { to: '/instructor/courses', icon: BookOpen, label: 'My Courses' },
  { to: '/instructor/assignments', icon: ClipboardList, label: 'Assignments' },
  { to: '/instructor/analytics', icon: BarChart3, label: 'Analytics' },
];

const studentLinks = [
  { to: '/student', icon: LayoutDashboard, label: 'Dashboard', end: true },
  { to: '/student/certificates', icon: Award, label: 'Certificates' },
];

export default function Sidebar() {
  const { user, logout } = useAuth();
  const [collapsed, setCollapsed] = useState(false);
  const [mobileOpen, setMobileOpen] = useState(false);
  const location = useLocation();

  const links = user?.role === 'super_admin' ? adminLinks : user?.role === 'instructor' ? instructorLinks : studentLinks;
  const roleLabel = user?.role === 'super_admin' ? 'Super Admin' : user?.role === 'instructor' ? 'Instructor' : 'Student';

  const sidebarContent = (
    <div className="flex flex-col h-full">
      <div className={`flex items-center ${collapsed ? 'justify-center' : 'justify-between'} p-4 border-b border-[#E7E5E4]`}>
        {!collapsed && (
          <div className="flex items-center gap-2">
            <div className="w-8 h-8 rounded-lg bg-[#312E81] flex items-center justify-center">
              <span className="text-white font-bold text-sm" style={{ fontFamily: 'Manrope' }}>E1</span>
            </div>
            <span className="font-bold text-lg text-[#312E81]" style={{ fontFamily: 'Manrope' }}>LMS</span>
          </div>
        )}
        <button onClick={() => setCollapsed(!collapsed)} className="hidden md:flex p-1 rounded hover:bg-[#F5F5F4] text-[#57534E] transition-colors" data-testid="sidebar-toggle">
          {collapsed ? <ChevronRight size={18} /> : <ChevronLeft size={18} />}
        </button>
      </div>

      {!collapsed && (
        <div className="px-4 py-3 border-b border-[#E7E5E4]">
          <p className="text-xs font-medium text-[#A8A29E] uppercase tracking-wider">{roleLabel}</p>
          <p className="text-sm font-medium text-[#1C1917] truncate mt-0.5">{user?.name}</p>
        </div>
      )}

      <nav className="flex-1 overflow-auto py-3 px-2 space-y-0.5">
        {links.map((link) => (
          <NavLink
            key={link.to}
            to={link.to}
            end={link.end}
            onClick={() => setMobileOpen(false)}
            className={({ isActive }) =>
              `flex items-center gap-3 px-3 py-2.5 rounded-lg text-sm font-medium transition-all duration-200 group
              ${isActive ? 'bg-[#EEF2FF] text-[#312E81]' : 'text-[#57534E] hover:bg-[#F5F5F4] hover:text-[#1C1917]'}`
            }
            data-testid={`nav-${link.label.toLowerCase().replace(/\s/g, '-')}`}
          >
            <link.icon size={18} className="shrink-0 transition-transform duration-200 group-hover:scale-110" />
            {!collapsed && <span>{link.label}</span>}
          </NavLink>
        ))}
      </nav>

      <div className="p-2 border-t border-[#E7E5E4]">
        <button onClick={logout} className="flex items-center gap-3 w-full px-3 py-2.5 rounded-lg text-sm font-medium text-[#57534E] hover:bg-red-50 hover:text-red-600 transition-all duration-200" data-testid="logout-btn">
          <LogOut size={18} />
          {!collapsed && <span>Logout</span>}
        </button>
      </div>
    </div>
  );

  return (
    <>
      <button onClick={() => setMobileOpen(true)} className="md:hidden fixed top-4 left-4 z-50 p-2 bg-white rounded-lg shadow-md" data-testid="mobile-menu-btn">
        <Menu size={20} />
      </button>

      {mobileOpen && <div className="md:hidden fixed inset-0 bg-black/30 z-40" onClick={() => setMobileOpen(false)} />}

      <aside className={`md:hidden fixed inset-y-0 left-0 z-50 w-64 bg-white border-r border-[#E7E5E4] transform transition-transform duration-300 ${mobileOpen ? 'translate-x-0' : '-translate-x-full'}`}>
        {sidebarContent}
      </aside>

      <aside className={`hidden md:flex flex-col bg-white border-r border-[#E7E5E4] transition-all duration-300 ${collapsed ? 'w-16' : 'w-60'}`} data-testid="sidebar">
        {sidebarContent}
      </aside>
    </>
  );
}
