import React, { useState, useEffect } from 'react';
import API from '../../api';
import { Plus, Search, Edit2, Trash2, X } from 'lucide-react';
import toast from 'react-hot-toast';

export default function Users({ role: filterRole }) {
  const [users, setUsers] = useState([]);
  const [loading, setLoading] = useState(true);
  const [search, setSearch] = useState('');
  const [showForm, setShowForm] = useState(false);
  const [editing, setEditing] = useState(null);
  const [form, setForm] = useState({ name: '', email: '', password: '', role: filterRole || 'student', bio: '' });

  const label = filterRole === 'instructor' ? 'Instructors' : 'Students';

  const fetchUsers = () => {
    API.get('/api/users', { params: { role: filterRole, search: search || undefined } })
      .then(r => setUsers(r.data)).catch(() => {}).finally(() => setLoading(false));
  };

  useEffect(() => { fetchUsers(); }, [search, filterRole]);

  const openEdit = (u) => { setForm({ name: u.name, email: u.email, password: '', role: u.role, bio: u.bio || '' }); setEditing(u.user_id); setShowForm(true); };
  const openNew = () => { setForm({ name: '', email: '', password: '', role: filterRole || 'student', bio: '' }); setEditing(null); setShowForm(true); };

  const saveUser = async () => {
    try {
      const data = { ...form };
      if (!data.password) delete data.password;
      if (editing) { await API.put(`/api/users/${editing}`, data); toast.success('User updated'); }
      else { if (!data.password) data.password = 'changeme123'; await API.post('/api/users', data); toast.success('User created'); }
      setShowForm(false); fetchUsers();
    } catch (e) { toast.error(e.response?.data?.detail || 'Error saving user'); }
  };

  const deleteUser = async (id) => {
    if (!window.confirm('Delete this user?')) return;
    try { await API.delete(`/api/users/${id}`); toast.success('User deleted'); fetchUsers(); } catch { toast.error('Error deleting user'); }
  };

  if (loading) return <div className="flex items-center justify-center h-64"><div className="animate-spin w-8 h-8 border-4 border-indigo-500 border-t-transparent rounded-full" /></div>;

  return (
    <div className="space-y-6 animate-fade-in" data-testid="users-page">
      <div className="flex items-center justify-between">
        <div>
          <h1 className="text-2xl font-bold text-[#1C1917]" style={{ fontFamily: 'Manrope' }}>{label}</h1>
          <p className="text-sm text-[#57534E] mt-1">{users.length} {label.toLowerCase()} total</p>
        </div>
        <button onClick={openNew} className="flex items-center gap-2 px-4 py-2 bg-[#312E81] text-white rounded-lg text-sm font-medium hover:bg-[#312E81]/90 active:scale-[0.98] transition-all" data-testid="create-user-btn"><Plus size={16} />Add {filterRole === 'instructor' ? 'Instructor' : 'Student'}</button>
      </div>

      <div className="relative">
        <Search size={16} className="absolute left-3 top-1/2 -translate-y-1/2 text-[#A8A29E]" />
        <input type="text" value={search} onChange={e => setSearch(e.target.value)} placeholder={`Search ${label.toLowerCase()}...`} className="w-full pl-9 pr-4 py-2.5 rounded-lg bg-white border border-[#E7E5E4] text-sm focus:border-[#6366F1] outline-none" data-testid="user-search" />
      </div>

      <div className="bg-white rounded-xl border border-[#E7E5E4] overflow-hidden">
        <table className="w-full text-sm">
          <thead>
            <tr className="border-b border-[#E7E5E4] bg-[#F5F5F4]">
              <th className="text-left py-3 px-4 text-xs font-semibold text-[#A8A29E] uppercase tracking-wider">Name</th>
              <th className="text-left py-3 px-4 text-xs font-semibold text-[#A8A29E] uppercase tracking-wider">Email</th>
              <th className="text-left py-3 px-4 text-xs font-semibold text-[#A8A29E] uppercase tracking-wider">Status</th>
              <th className="text-left py-3 px-4 text-xs font-semibold text-[#A8A29E] uppercase tracking-wider">Joined</th>
              <th className="text-right py-3 px-4 text-xs font-semibold text-[#A8A29E] uppercase tracking-wider">Actions</th>
            </tr>
          </thead>
          <tbody>
            {users.map(u => (
              <tr key={u.user_id} className="border-b border-[#F5F5F4] hover:bg-[#FAFAF9] transition-colors" data-testid={`user-row-${u.user_id}`}>
                <td className="py-3 px-4">
                  <div className="flex items-center gap-3">
                    <div className="w-8 h-8 rounded-full bg-[#EEF2FF] flex items-center justify-center text-[#6366F1] font-semibold text-xs">{u.name?.charAt(0)}</div>
                    <div>
                      <p className="font-medium text-[#1C1917]">{u.name}</p>
                      {u.bio && <p className="text-xs text-[#A8A29E] truncate max-w-[200px]">{u.bio}</p>}
                    </div>
                  </div>
                </td>
                <td className="py-3 px-4 text-[#57534E]">{u.email}</td>
                <td className="py-3 px-4"><span className={`px-2 py-0.5 rounded-full text-xs font-medium ${u.status === 'active' ? 'bg-green-100 text-green-700' : 'bg-red-100 text-red-700'}`}>{u.status}</span></td>
                <td className="py-3 px-4 text-[#A8A29E]">{new Date(u.created_at).toLocaleDateString()}</td>
                <td className="py-3 px-4 text-right">
                  <div className="flex items-center justify-end gap-1">
                    <button onClick={() => openEdit(u)} className="p-1.5 rounded-lg hover:bg-[#F5F5F4] text-[#57534E]" data-testid={`edit-user-${u.user_id}`}><Edit2 size={14} /></button>
                    <button onClick={() => deleteUser(u.user_id)} className="p-1.5 rounded-lg hover:bg-red-50 text-red-400" data-testid={`delete-user-${u.user_id}`}><Trash2 size={14} /></button>
                  </div>
                </td>
              </tr>
            ))}
            {users.length === 0 && <tr><td colSpan={5} className="text-center py-8 text-[#A8A29E]">No {label.toLowerCase()} found</td></tr>}
          </tbody>
        </table>
      </div>

      {showForm && (
        <div className="fixed inset-0 bg-black/30 z-50 flex items-center justify-center p-4" onClick={() => setShowForm(false)}>
          <div className="bg-white rounded-xl shadow-xl w-full max-w-md" onClick={e => e.stopPropagation()}>
            <div className="flex items-center justify-between p-5 border-b border-[#E7E5E4]">
              <h3 className="text-lg font-semibold" style={{ fontFamily: 'Manrope' }}>{editing ? 'Edit' : 'Create'} {filterRole === 'instructor' ? 'Instructor' : 'Student'}</h3>
              <button onClick={() => setShowForm(false)} className="p-1 rounded hover:bg-[#F5F5F4]"><X size={18} /></button>
            </div>
            <div className="p-5 space-y-4">
              <div><label className="block text-sm font-medium mb-1">Full Name *</label><input value={form.name} onChange={e => setForm({...form, name: e.target.value})} className="w-full px-3 py-2 rounded-lg border border-[#E7E5E4] text-sm outline-none focus:border-[#6366F1]" data-testid="user-name-input" /></div>
              <div><label className="block text-sm font-medium mb-1">Email *</label><input type="email" value={form.email} onChange={e => setForm({...form, email: e.target.value})} className="w-full px-3 py-2 rounded-lg border border-[#E7E5E4] text-sm outline-none focus:border-[#6366F1]" data-testid="user-email-input" /></div>
              <div><label className="block text-sm font-medium mb-1">{editing ? 'New Password (optional)' : 'Password'}</label><input type="password" value={form.password} onChange={e => setForm({...form, password: e.target.value})} className="w-full px-3 py-2 rounded-lg border border-[#E7E5E4] text-sm outline-none focus:border-[#6366F1]" placeholder={editing ? 'Leave blank to keep current' : 'Enter password'} data-testid="user-password-input" /></div>
              <div><label className="block text-sm font-medium mb-1">Bio</label><textarea value={form.bio} onChange={e => setForm({...form, bio: e.target.value})} rows={2} className="w-full px-3 py-2 rounded-lg border border-[#E7E5E4] text-sm outline-none focus:border-[#6366F1] resize-none" data-testid="user-bio-input" /></div>
            </div>
            <div className="flex justify-end gap-2 p-5 border-t border-[#E7E5E4]">
              <button onClick={() => setShowForm(false)} className="px-4 py-2 text-sm font-medium text-[#57534E] hover:bg-[#F5F5F4] rounded-lg">Cancel</button>
              <button onClick={saveUser} className="px-4 py-2 text-sm font-medium text-white bg-[#312E81] rounded-lg hover:bg-[#312E81]/90 active:scale-[0.98] transition-all" data-testid="save-user-btn">Save</button>
            </div>
          </div>
        </div>
      )}
    </div>
  );
}
