import React, { useState } from 'react';
import { useNavigate } from 'react-router-dom';
import { useAuth } from '../context/AuthContext';
import { BookOpen, Eye, EyeOff } from 'lucide-react';
import toast from 'react-hot-toast';

export default function Login() {
  const { login, loginWithGoogle, user } = useAuth();
  const navigate = useNavigate();
  const [email, setEmail] = useState('');
  const [password, setPassword] = useState('');
  const [showPw, setShowPw] = useState(false);
  const [loading, setLoading] = useState(false);
  const [tab, setTab] = useState('login');

  if (user) {
    const path = user.role === 'super_admin' ? '/admin' : user.role === 'instructor' ? '/instructor' : '/student';
    navigate(path, { replace: true });
    return null;
  }

  const handleSubmit = async (e) => {
    e.preventDefault();
    setLoading(true);
    try {
      const u = await login(email, password);
      toast.success(`Welcome back, ${u.name}!`);
      navigate(u.role === 'super_admin' ? '/admin' : u.role === 'instructor' ? '/instructor' : '/student');
    } catch (err) {
      toast.error(err.response?.data?.detail || 'Login failed');
    } finally {
      setLoading(false);
    }
  };

  return (
    <div className="min-h-screen flex bg-[#FAFAF9]" data-testid="login-page">
      <div className="hidden lg:flex lg:w-1/2 relative overflow-hidden" style={{ background: 'linear-gradient(135deg, #312E81 0%, #6366F1 50%, #818CF8 100%)' }}>
        <div className="absolute inset-0 opacity-10" style={{ backgroundImage: 'radial-gradient(circle at 25px 25px, white 2px, transparent 0)', backgroundSize: '50px 50px' }} />
        <div className="relative z-10 flex flex-col justify-center px-16">
          <div className="flex items-center gap-3 mb-12">
            <div className="w-12 h-12 rounded-xl bg-white/20 backdrop-blur flex items-center justify-center">
              <BookOpen className="text-white" size={24} />
            </div>
            <span className="text-3xl font-bold text-white" style={{ fontFamily: 'Manrope' }}>E1 LMS</span>
          </div>
          <h1 className="text-5xl font-bold text-white leading-tight mb-6" style={{ fontFamily: 'Manrope' }}>
            Empower Your<br />Learning Journey
          </h1>
          <p className="text-lg text-indigo-100 leading-relaxed max-w-md">
            A comprehensive learning management system designed for modern education. Track progress, manage courses, and achieve your goals.
          </p>
          <div className="mt-12 grid grid-cols-3 gap-6">
            {[{ n: '500+', l: 'Students' }, { n: '50+', l: 'Courses' }, { n: '95%', l: 'Completion' }].map(s => (
              <div key={s.l} className="bg-white/10 backdrop-blur rounded-xl p-4">
                <p className="text-2xl font-bold text-white">{s.n}</p>
                <p className="text-sm text-indigo-200">{s.l}</p>
              </div>
            ))}
          </div>
        </div>
      </div>

      <div className="flex-1 flex items-center justify-center px-6">
        <div className="w-full max-w-md">
          <div className="lg:hidden flex items-center gap-2 mb-8">
            <div className="w-10 h-10 rounded-xl bg-[#312E81] flex items-center justify-center">
              <BookOpen className="text-white" size={20} />
            </div>
            <span className="text-2xl font-bold text-[#312E81]" style={{ fontFamily: 'Manrope' }}>E1 LMS</span>
          </div>

          <h2 className="text-3xl font-bold text-[#1C1917] mb-2" style={{ fontFamily: 'Manrope' }}>
            {tab === 'login' ? 'Welcome back' : 'Get started'}
          </h2>
          <p className="text-[#57534E] mb-8">{tab === 'login' ? 'Sign in to your account' : 'Create your account'}</p>

          <button onClick={loginWithGoogle} className="w-full flex items-center justify-center gap-3 px-4 py-3 border border-[#E7E5E4] rounded-lg hover:bg-[#F5F5F4] transition-all duration-200 mb-6 hover:border-[#D6D3D1] active:scale-[0.98]" data-testid="google-login-btn">
            <svg width="20" height="20" viewBox="0 0 24 24"><path d="M22.56 12.25c0-.78-.07-1.53-.2-2.25H12v4.26h5.92a5.06 5.06 0 01-2.2 3.32v2.77h3.57c2.08-1.92 3.28-4.74 3.28-8.1z" fill="#4285F4"/><path d="M12 23c2.97 0 5.46-.98 7.28-2.66l-3.57-2.77c-.98.66-2.23 1.06-3.71 1.06-2.86 0-5.29-1.93-6.16-4.53H2.18v2.84C3.99 20.53 7.7 23 12 23z" fill="#34A853"/><path d="M5.84 14.09c-.22-.66-.35-1.36-.35-2.09s.13-1.43.35-2.09V7.07H2.18C1.43 8.55 1 10.22 1 12s.43 3.45 1.18 4.93l2.85-2.22.81-.62z" fill="#FBBC05"/><path d="M12 5.38c1.62 0 3.06.56 4.21 1.64l3.15-3.15C17.45 2.09 14.97 1 12 1 7.7 1 3.99 3.47 2.18 7.07l3.66 2.84c.87-2.6 3.3-4.53 6.16-4.53z" fill="#EA4335"/></svg>
            <span className="font-medium text-[#1C1917]">Continue with Google</span>
          </button>

          <div className="flex items-center gap-3 mb-6">
            <div className="flex-1 h-px bg-[#E7E5E4]" />
            <span className="text-xs text-[#A8A29E] uppercase tracking-wider font-medium">or</span>
            <div className="flex-1 h-px bg-[#E7E5E4]" />
          </div>

          <form onSubmit={handleSubmit} className="space-y-4">
            <div>
              <label className="block text-sm font-medium text-[#1C1917] mb-1.5">Email</label>
              <input type="email" value={email} onChange={e => setEmail(e.target.value)} required
                className="w-full px-4 py-2.5 rounded-lg border border-[#E7E5E4] bg-white text-sm focus:border-[#6366F1] focus:ring-2 focus:ring-[#EEF2FF] outline-none transition-all"
                placeholder="you@example.com" data-testid="login-email" />
            </div>
            <div>
              <label className="block text-sm font-medium text-[#1C1917] mb-1.5">Password</label>
              <div className="relative">
                <input type={showPw ? 'text' : 'password'} value={password} onChange={e => setPassword(e.target.value)} required
                  className="w-full px-4 py-2.5 rounded-lg border border-[#E7E5E4] bg-white text-sm focus:border-[#6366F1] focus:ring-2 focus:ring-[#EEF2FF] outline-none transition-all pr-10"
                  placeholder="Enter password" data-testid="login-password" />
                <button type="button" onClick={() => setShowPw(!showPw)} className="absolute right-3 top-1/2 -translate-y-1/2 text-[#A8A29E] hover:text-[#57534E]">
                  {showPw ? <EyeOff size={16} /> : <Eye size={16} />}
                </button>
              </div>
            </div>
            <button type="submit" disabled={loading}
              className="w-full py-2.5 bg-[#312E81] text-white rounded-lg font-medium text-sm hover:bg-[#312E81]/90 active:scale-[0.98] transition-all disabled:opacity-50"
              data-testid="login-submit-btn">
              {loading ? 'Signing in...' : 'Sign In'}
            </button>
          </form>

          <div className="mt-6 p-4 bg-[#EEF2FF] rounded-xl">
            <p className="text-xs font-semibold text-[#312E81] mb-2">Demo Accounts</p>
            <div className="space-y-1 text-xs text-[#57534E]">
              <p><span className="font-medium">Admin:</span> admin@lms.com / admin123</p>
              <p><span className="font-medium">Instructor:</span> sarah@lms.com / instructor123</p>
              <p><span className="font-medium">Student:</span> liam@student.com / student123</p>
            </div>
          </div>
        </div>
      </div>
    </div>
  );
}
