import React, { useState, useEffect } from 'react';
import { useAuth } from '../context/AuthContext';
import { Bell, Search, ChevronDown } from 'lucide-react';
import API from '../api';

export default function Header() {
  const { user, logout } = useAuth();
  const [showProfile, setShowProfile] = useState(false);
  const [unreadCount, setUnreadCount] = useState(0);

  useEffect(() => {
    API.get('/api/notifications').then(res => {
      setUnreadCount(res.data.filter(n => !n.is_read).length);
    }).catch(() => {});
  }, []);

  return (
    <header className="h-14 bg-white border-b border-[#E7E5E4] flex items-center justify-between px-6 shrink-0" data-testid="header">
      <div className="flex items-center gap-3">
        <div className="relative hidden md:block">
          <Search size={16} className="absolute left-3 top-1/2 -translate-y-1/2 text-[#A8A29E]" />
          <input
            type="text"
            placeholder="Search..."
            className="pl-9 pr-4 py-1.5 w-64 rounded-lg bg-[#F5F5F4] border border-transparent focus:border-[#6366F1] focus:bg-white text-sm outline-none transition-all"
            data-testid="header-search"
          />
        </div>
      </div>

      <div className="flex items-center gap-4">
        <button className="relative p-2 rounded-lg hover:bg-[#F5F5F4] transition-colors" data-testid="notifications-bell">
          <Bell size={18} className="text-[#57534E]" />
          {unreadCount > 0 && (
            <span className="absolute -top-0.5 -right-0.5 w-4 h-4 bg-[#EF4444] rounded-full text-white text-[10px] flex items-center justify-center font-medium">{unreadCount}</span>
          )}
        </button>

        <div className="relative">
          <button onClick={() => setShowProfile(!showProfile)} className="flex items-center gap-2 px-2 py-1 rounded-lg hover:bg-[#F5F5F4] transition-colors" data-testid="profile-menu">
            <div className="w-7 h-7 rounded-full bg-[#312E81] flex items-center justify-center">
              <span className="text-white text-xs font-semibold">{user?.name?.charAt(0) || 'U'}</span>
            </div>
            <span className="text-sm font-medium text-[#1C1917] hidden md:block">{user?.name}</span>
            <ChevronDown size={14} className="text-[#A8A29E]" />
          </button>
          {showProfile && (
            <>
              <div className="fixed inset-0" onClick={() => setShowProfile(false)} />
              <div className="absolute right-0 top-full mt-1 w-48 bg-white rounded-xl shadow-lg border border-[#E7E5E4] py-1 z-50 animate-fade-in">
                <div className="px-3 py-2 border-b border-[#E7E5E4]">
                  <p className="text-sm font-medium">{user?.name}</p>
                  <p className="text-xs text-[#A8A29E]">{user?.email}</p>
                </div>
                <button onClick={logout} className="w-full text-left px-3 py-2 text-sm text-red-600 hover:bg-red-50 transition-colors" data-testid="profile-logout">Logout</button>
              </div>
            </>
          )}
        </div>
      </div>
    </header>
  );
}
